class Solution {
public:
      long long int go(int i,vector<pair<int,int>>&k,int sum,vector<vector<long long int>>&dp){
          if(i==k.size()){
              if(sum==0)return 0;
              return 2e5;
          }

          if(dp[i][sum]!=-1)return dp[i][sum];
          long long int ans=2e5;
          int a=k[i].first;

          int b=k[i].second;
          ans=min(ans,a+go(i+1,k,sum,dp));

          ans=min(ans,b-a+go(i+1,k,(sum+b)%4,dp));

          return dp[i][sum]=ans;

      }
    int minFlips(vector<vector<int>>& v) {
        int n=v.size();
        int m=v[0].size();
        int ans=0;
        vector<pair<int,int>>k;
    for(int i = 0; i <= (n-1)/2; i++){
        for(int j = 0; j <= (m-1)/2; j++){
            set<pair<int,int>>st;

            st.insert({i,j});
            st.insert({n-i-1,j});

            st.insert({n-i-1,m-j-1});
            st.insert({i,m-j-1});

            int sum=0;
            for(auto x:st){
                sum+=v[x.first][x.second];
            }
            k.push_back({sum,st.size()});
        }
    }

        n=k.size();
        vector<vector<long long int>>dp(n,vector<long long int>(4,-1));
        return go(0,k,0,dp);
    }
};
