class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int oneCount = 0;
        int ans = 0;
        int spare = 0;
        for(int i = 0; i < (n/2); i++) {
            for(int j = 0; j < (m/2); j++) {
                int oc = 0;
                int zc = 0;
                int lu = grid[i][j];
                int ru = grid[i][m-j-1];
                int ld = grid[n-i-1][j];
                int rd = grid[n-i-1][m-j-1];
                if(lu) oc++;
                else zc++;
                if(ru)  oc++;
                else zc++;
                if(ld) oc++;
                else zc++;
                if(rd)  oc++;
                else zc++;

                if(oc != 4 || zc != 4) {
                    ans+=(4 - max(oc, zc));
                }
            }
        }
        if(m%2){
            for(int i = 0; i < (n/2); i++) {
                if(grid[i][m/2] == grid[n-i-1][m/2]) {
                    if(grid[i][m/2])
                        oneCount += 2;
                }
                else {
                    ans += 1;
                    spare += 1;
                }
            }
        }
        if(n%2) {
            for(int i = 0; i < (m/2); i++) {
                if(grid[n/2][i] == grid[n/2][m-i-1]) {
                    if(grid[n/2][i])
                        oneCount += 2;
                }
                else {
                    ans += 1;
                    spare += 1;
                }
            }
        }
        if(n%2 && m%2) {
            if(grid[n/2][m/2])  ans++;
        }
        if(oneCount%4 && !spare) {
            ans += 2;
        }

        return ans;
    }
};
