class Solution {
    public int minFlips(int[][] grid) {
        int count = 0;
        for (int i = 0; i < grid.length / 2; i++) {
            for  (int j = 0; j < grid[0].length / 2; j++) {
                int sum = grid[i][j] + grid[grid.length - 1 - i][j] + grid[i][grid[0].length - 1 - j] + grid[grid.length - 1 - i][grid[0].length - 1 - j];
                count += Math.min(sum, 4 - sum);
            }
        }
        int sum = 0;
        boolean check1 = true;
        if (grid.length % 2 == 1) {
            for (int i = 0; i < grid[0].length / 2; i++) {
                if (grid[grid.length / 2][i] != grid[grid.length / 2][grid[0].length - 1 - i]) {
                    check1 = false;
                    count++;
                } else {
                    sum += 2 * grid[grid.length / 2][i];
                }
            }
        }
        boolean check2 = true;
        if (grid[0].length % 2 == 1) {
            for (int i = 0; i < grid.length / 2; i++) {
                if (grid[i][grid[0].length / 2] != grid[grid.length - 1 - i][grid[0].length / 2]) {
                    check2 = false;
                    count++;
                } else {
                    sum += 2 * grid[i][grid[0].length / 2];
                }
            }
        }

        if (check1 && check2 && sum % 4 != 0) {
            count += 2;
        }

        if (grid.length % 2 == 1 && grid[0].length % 2 == 1) {
            count += grid[grid.length / 2][grid[0].length / 2];
        }
        return count;
    }
}
