class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;

        // Check the center if both n and m are odd
        if (n & 1 && m & 1) {
            ans += (grid[n / 2][m / 2] == 1) ? 1 : 0;
        }

        array<int, 3> shift = {0, 0, 0};
        ans += handleRow(n, m, grid, shift);
        ans += handleColumn(n, m, grid, shift);

        if (shift[2] & 1) {
            ans += (shift[1] > 0) ? shift[1] : 2;
        } else {
            ans += shift[1];
        }

        ans += handleQuadrants(n, m, grid);
        return ans;
    }

private:
    int handleRow(int n, int m, vector<vector<int>>& grid, array<int, 3>& shift) {
        if (n & 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int sm = grid[n / 2][a] + grid[n / 2][b];
                shift[sm]++;
                ++a;
                --b;
            }
        }
        return 0;
    }

    int handleColumn(int n, int m, vector<vector<int>>& grid, array<int, 3>& shift) {
        if (m & 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int sm = grid[a][m / 2] + grid[b][m / 2];
                shift[sm]++;
                ++a;
                --b;
            }
        }
        return 0;
    }

    int handleQuadrants(int n, int m, vector<vector<int>>& grid) {
        int ans = 0;
        int i = 0;
        while (i < n / 2) {
            int j = 0;
            while (j < m / 2) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int sm = a + b + c + d;
                if (sm != 0 && sm != 4) {
                    ans += (sm < 2) ? sm : 4 - sm;
                }
                ++j;
            }
            ++i;
        }
        return ans;
    }
};
