class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int m=grid[0].size();
        int n=grid.size();
        int c11=0,c12=0,c10=0;
        vector<vector<int>> vis(n, vector<int> (m,0));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                int o=0;
                int c=0;
                if(!vis[i][j]){
                    // cout<<i<<" "<<j<<endl;
                    if(grid[i][j])
                    c++;
                    o++;
                    vis[i][j]=1;
                }
                if(!vis[n-i-1][j]){
                    // cout<<n-i-1<<" "<<j<<endl;
                    if(grid[n-i-1][j])
                    c++;
                    o++;
                    vis[n-i-1][j]=1;
                }
                if(!vis[i][m-j-1]){
                    // cout<<i<<" "<<m-j-1<<endl;
                    if(grid[i][m-j-1])
                    c++;
                    o++;
                    vis[i][m-j-1]=1;
                }
                if(!vis[n-i-1][m-j-1]){
                    // cout<<n-i-1<<" "<<m-j-1<<endl;
                    if(grid[n-i-1][m-j-1])
                    c++;
                    o++;
                    vis[n-i-1][m-j-1]=1;
                }
                // cout<<o<<" k "<<c<<endl;
                if(o==4){
                    ans+=min(c,o-c);
                }
                else if(o==1){
                    ans+=c;
                }
                else if(o==2){
                    if(c==0){
                        c10++;
                    }
                    else if(c==1){
                        c11++;
                    }
                    else{
                        c12++;
                    }
                }
            }
        }
        // cout<<ans<<" "<<c10<<" "<<c11<<" "<<c12<<endl;
        if(c12%2==0){
            ans+=c11;
        }
        else{
            if(c11>=1){
                ans+=c11;
            }
            else{
                ans+=2;
            }
        }
        return ans;
    }
};
