class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ret = 0 , row = grid.size(), col = grid[0].size();
        for(int i=0;i<row/2;i++){
            for(int j=0;j<col/2;j++){
                int val = grid[i][j] + grid[i][col - j -1] + grid[row - i - 1][j] + grid[row - i - 1][col - j -1];
                ret += (val > 4 - val) ? 4 - val : val; 
            }
        }
        bool has01 = false;
        int nums1 = 0;
        if(row%2 == 1){
            for(int i=0;i<col/2;i++){
                if(grid[row/2][i] != grid[row/2][col - i - 1]){
                    has01 = true;
                    ret ++;
                    //nums1 +=2 ;
                }
                else if ( grid[row/2][i] == 1) nums1 += 2;
            }
        }
        if(col%2 == 1){
            for(int i=0;i<row/2;i++){
                if(grid[i][col/2] != grid[row - i - 1][col/2]){
                    has01 = true;
                    ret ++;
                    //nums1 += 2;
                }
                else if ( grid[i][col/2] == 1) nums1 += 2;
            }
        }
        if(col%2 == 1 && row%2 == 1 && grid[row/2][col/2] == 1) ret++;
        if(nums1 % 4 != 0) ret += (has01)?0:2;
        return ret;
    }
};
