from typing import List

class Solution:
    def minFlips(self, matrix: List[List[int]]) -> int:
        numRows = len(matrix)
        numCols = len(matrix[0])

        totalFlips = 0
        caseA = 0
        caseB = 0
        caseC = 0

        caseD = 0
        caseE = 0

        for row in range((numRows + 1) // 2):
            for col in range((numCols + 1) // 2):
                if row == numRows - 1 - row and col == numCols - 1 - col:
                    if matrix[row][col] == 1:
                        totalFlips += 1
                elif row == numRows - 1 - row:
                    val = matrix[row][col] + matrix[row][numCols - 1 - col]
                    if val == 1:
                        caseD += 1
                    elif val == 2:
                        caseE += 1
                elif col == numCols - 1 - col:
                    val = matrix[row][col] + matrix[numRows - 1 - row][col]
                    if val == 1:
                        caseD += 1
                    elif val == 2:
                        caseE += 1
                else:
                    val = (matrix[row][col] + matrix[numRows - 1 - row][col] +
                           matrix[row][numCols - 1 - col] + matrix[numRows - 1 - row][numCols - 1 - col])
                    if val == 1:
                        caseA += 1
                    elif val == 2:
                        caseB += 1
                    elif val == 3:
                        caseC += 1

        totalFlips += (caseA + caseC + 2 * caseB + caseD)

        if caseE % 2 != 0:
            if caseD <= 0:
                totalFlips += 2

        return totalFlips
