class Solution {
public:

    int Solve(const vector<int>& V, bool target_parity) {
        int n=V.size();
        int counts_00=0;
        int counts_01=0;
        int counts_11=0;
        if (n%2) {
            printf("err\n");
            exit(0);
        }
        for(int i=0;i<n-1-i;i++) {
            int j=n-1-i;
            if (V[i]==0 && V[j]==0) {
                counts_00++;
            } else if (V[i]!=V[j]) {
                counts_01++;
            } else {
                counts_11++;
            }
        }

        int ret = (1<<30);
        for(int x=0;x<=counts_01;x++) {
            int no = (counts_11 + (counts_01-x));
            int curr = counts_01;
            if (no%2 != target_parity) {
                curr+=2;
            }
            ret=min(ret,curr);
        }
        return ret;
    }

    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int n=grid.size();
        int m=grid[0].size();
        for(int i=0;i<n-1-i;i++) {
            for(int j=0;j<m-1-j;j++) {
                // n-1-i
                // m-1-j
                int counts[2];
                counts[0]=counts[1]=0;
                for(int ii : {i,n-1-i}) {
                    for(int jj : {j,m-1-j}) {
                        counts[grid[ii][jj]]++;
                    }
                }
                ans += 4-max(counts[0],counts[1]);
            }
        }

        if (n%2==0 && m%2==0) {
            return ans;
        }

        if (n%2 && m%2) {
            ans += grid[n/2][m/2];
            vector<int> V,W;
            for(int i=0;i<n;i++) {
                if (i!=n/2)
                V.push_back(grid[i][m/2]);
            }
            for(int j=0;j<m;j++) {
                if(j!=m/2)
                W.push_back(grid[n/2][j]);
            }

            ans += min(Solve(V,0)+Solve(W,0),Solve(V,1)+Solve(W,1));

            return ans;
        }


        vector<int> V;
        if (n%2) {
            for(int j=0;j<m;j++) {
                V.push_back(grid[n/2][j]);
            }
        } else {
            for(int i=0;i<n;i++) {
                V.push_back(grid[i][m/2]);
            }
        }
        return ans+Solve(V,0);
    }
};
