class Solution:
    def minFlips(self, g: List[List[int]]) -> int:
        r, c = len(g), len(g[0])
        f = 0

        if r % 2 == 1 and c % 2 == 1:
            if g[r // 2][c // 2] == 1:
                f += 1

        sc = [0] * 3
        if r % 2 == 1:
            l, ri = 0, c - 1
            while l < ri:
                scr = g[r // 2][l] + g[r // 2][ri]
                sc[scr] += 1
                l += 1
                ri -= 1

        if c % 2 == 1:
            t, b = 0, r - 1
            while t < b:
                scc = g[t][c // 2] + g[b][c // 2]
                sc[scc] += 1
                t += 1
                b -= 1

        if sc[2] % 2 == 1:
            if sc[1] > 0:
                f += sc[1]
            else:
                f += 2
        else:
            f += sc[1]

        for i in range(r // 2):
            for j in range(c // 2):
                tl = g[i][j]
                bl = g[r - 1 - i][j]
                tr = g[i][c - 1 - j]
                br = g[r - 1 - i][c - 1 - j]

                scn = tl + bl + tr + br

                if scn != 0 and scn != 4:
                    f += min(scn, 4 - scn)

        return f
