void fun1(int** g, int n, int m, int* s) {
    int a = 0, b = m - 1;
    while (a < b) {
        int sm = g[n / 2][a] + g[n / 2][b];
        s[sm] += 1;
        a += 1;
        b -= 1;
    }
}

void fun2(int** g, int n, int m, int* s) {
    int a = 0, b = n - 1;
    while (a < b) {
        int sm = g[a][m / 2] + g[b][m / 2];
        s[sm] += 1;
        a += 1;
        b -= 1;
    }
}

int fun3(int* s) {
    int ans = 0;
    if (s[2] % 2 == 1) {
        if (s[1] > 0) {
            ans += s[1];
        } else {
            ans += 2;
        }
    } else {
        ans += s[1];
    }
    return ans;
}

int fun4(int** g, int n, int m) {
    int ans = 0;
    int i = 0, j = 0;
    while (i < n / 2) {
        j = 0;
        while (j < m / 2) {
            int a = g[i][j];
            int b = g[n - 1 - i][j];
            int c = g[i][m - 1 - j];
            int d = g[n - 1 - i][m - 1 - j];
            int sm = a + b + c + d;
            if (sm != 0 && sm != 4) {
                ans += (sm < 4 - sm) ? sm : 4 - sm;
            }
            j += 1;
        }
        i += 1;
    }
    return ans;
}

int minFlips(int** grid, int gridSize, int* gridColSize) {
    int n = gridSize;
    int m = gridColSize[0];
    int ans = 0;

    if (n % 2 == 1 && m % 2 == 1) {
        if (grid[n / 2][m / 2] == 1) {
            ans += 1;
        }
    }

    int s[3] = {0, 0, 0};
    if (n % 2 == 1) {
        fun1(grid, n, m, s);
    }

    if (m % 2 == 1) {
        fun2(grid, n, m, s);
    }

    ans += fun3(s);
    ans += fun4(grid, n, m);

    return ans;
}
