class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();

        int totalFlips = 0;

        // Loop through the 2x2 sub-grids
        for (int r = 0; r < rows / 2; ++r) {
            for (int c = 0; c < cols / 2; ++c) {
                vector<int> subGrid = {
                    grid[r][c],
                    grid[rows - r - 1][c],
                    grid[r][cols - c - 1],
                    grid[rows - r - 1][cols - c - 1]
                };

                int onesCount = count(subGrid.begin(), subGrid.end(), 1);

                totalFlips += (onesCount < 4 - onesCount) ? onesCount : (4 - onesCount);
            }
        }

        int potentialFlips = 0;
        int singleOnes = 0;

                if (rows % 2 != 0) {
            int midRow = rows / 2;
            for (int c = 0; c < cols / 2; ++c) {
                totalFlips += (grid[midRow][c] != grid[midRow][cols - c - 1]) ? 1 : 0;
                potentialFlips += (grid[midRow][c] != grid[midRow][cols - c - 1]) ? 1 : 0;
                singleOnes += (grid[midRow][c] == 1 && grid[midRow][cols - c - 1] == 1) ? 1 : 0;
            }
        }

                if (cols % 2 != 0) {
            int midCol = cols / 2;
            for (int r = 0; r < rows / 2; ++r) {
                totalFlips += (grid[r][midCol] != grid[rows - r - 1][midCol]) ? 1 : 0;
                potentialFlips += (grid[r][midCol] != grid[rows - r - 1][midCol]) ? 1 : 0;
                singleOnes += (grid[r][midCol] == 1 && grid[rows - r - 1][midCol] == 1) ? 1 : 0;
            }
        }

              if (rows % 2 != 0 && cols % 2 != 0 && grid[rows / 2][cols / 2] == 1) {
            totalFlips += 1;
        }

        if (potentialFlips == 0 && singleOnes % 2 != 0) {
            totalFlips += 2;
        }

        return totalFlips;
    }
};
