class Solution {
public:
     int minFlips(vector<vector<int>>& grid) {
         int n = grid.size(),m=grid[0].size();
         int flp=0;
         int cnt=0;
             for (int i=0;i<n;i++){ 
                      for (int j=0;j<m;j++){ 
                        if ((j == m-j-1) && (i == n-i-1)){
                           if (grid[i][j] == 1) {
                              flp++; grid[i][j] =0;
                           }
                        }
                        else if (i == n-i-1){
                           int ele1 = grid[i][j];
                           int ele2 = grid[i][m-j-1];
                           if (ele1 ==1 && ele2==0){
                              grid[i][j] = 0; flp++;
                              cnt++;
                           }
                          else if (ele2 ==1 && ele1==0){
                              grid[i][m-j-1] = 0; flp++;
                              cnt++;
                           }
                        }
                        else if (j == m-j-1){
                           int ele1 = grid[i][j];
                           int ele2 = grid[n-i-1][j];
                           if (ele1 ==1 && ele2==0){
                              grid[i][j] = 0; flp++;
                              cnt++;
                           }
                          else if (ele2 ==1 && ele1==0){
                              grid[n-i-1][j] = 0; flp++;
                              cnt++;
                           }
                        }
                        else {
                           vector<int> ele = {grid[i][j],grid[i][m-j-1],grid[n-i-1][j],grid[n-i-1][m-j-1]};
                           int on =0,zr=0;
                           for(auto vl : ele){
                              if (vl==1) on++;
                              else zr++;
                           }
                           if (on == 2){
                           flp+=2;
                   grid[i][j]=grid[i][m-j-1]=grid[n-i-1][j]=grid[n-i-1][m-j-1]=0;
                           }
                           else  if (zr == 1 ||zr==3){
                             grid[i][j]=grid[i][m-j-1]=grid[n-i-1][j]=grid[n-i-1][m-j-1]=0;
                             flp++; 
                           }
                        }
                      } 
             } 
               int one=0;
                 for (int i=0;i<n;i++){ 
                      for (int j=0;j<m;j++){ 
                        if (grid[i][j]==1) one++;
                      } 
                } 
                if (one%4==0) return flp;
                if (cnt>0) return flp;
                return flp+2;
    }

};
