class Solution
{
public:
    int minFlips(vector<vector<int>> &grid)
    {
        int m = grid.size();
        int n = grid[0].size();
        int ans = 0;

        for (int i = 0; i < m / 2; i++)
        {
            for (int j = 0; j < n / 2; j++)
            {

                int s = 0;
                s += grid[i][j];
                s += grid[i][n - 1 - j];
                s += grid[m - 1 - i][j];
                s += grid[m - 1 - i][n - 1 - j];

                if (s == 1 || s == 3)
                {
                    ans += 1;
                }
                else if (s == 2)
                {
                    ans += 2;
                }
            }
        }

        int c1 = 0, c2 = 0;
        int one = 0;
        if (m % 2 == 1)
        {
            int f = 0, l = n - 1;
            while (f < l)
            {
                if (grid[m / 2][f] != grid[m / 2][l])
                {
                    c1++;
                }
                if (grid[m / 2][f] == 1 && grid[m / 2][l] == 1)
                    one += 2;
                f++;
                l--;
            }
        }

        if (n % 2 == 1)
        {
            int f = 0, l = m - 1;
            while (f < l)
            {
                if (grid[f][n / 2] != grid[l][n / 2])
                {
                    c2++;
                }
                if (grid[f][n / 2] == 1 && grid[l][n / 2] == 1)
                    one += 2;
                f++;
                l--;
            }
        }
        if (one % 4 != 0)
        {
            if (c1 + c2 == 0)
                ans += 2;
        }

        if (m % 2 == 1 && n % 2 == 1)
        {
            if (grid[m / 2][n / 2] == 1)
            {
                ans++;
            }
        }
        return ans + c1 + c2;
    }
};
