class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        vector<vector<int>> arr(n, vector<int> (m, 0));
        int ans = 0;

        for(int i = 0;i < (n)/2; i++){
            for(int j = 0; j < (m)/2;j++){
                vector<int> tmp(2,0);
                tmp[grid[i][j]]++;
                tmp[grid[i][m-j-1]]++;
                tmp[grid[n-i-1][j]]++;
                tmp[grid[n-i-1][m-j-1]]++;
                ans += min(tmp[0], tmp[1]);
                arr[i][j] = 1;
                arr[i][m-1-j] = 1;
                arr[n-1-i][j] = 1;
                arr[n-1-i][m-1-j] = 1;
            }
        }
        int c1 = 0, cnt = 0 , xx = 0;
        if(n % 2){
            for(int j = 0; j < m/2; j++){
                if(grid[n/2][j] != grid[n/2][m-j-1]){
                    cnt++;
                }
                else if(grid[n/2][j]==1){
                    xx++;
                }
            }
        }
        if(m % 2){
            for(int i = 0; i < n/2;i++){
                if(grid[i][m/2] != grid[n-i-1][m/2]){
                    cnt++;
                }
                else if(grid[i][m/2]==1){
                    xx++;
                }
            }
        }
        if(n%2 && m%2 && grid[n/2][m/2] == 1){
            ans++;
        }
        if(xx % 2 && cnt == 0){
            ans += 2;
        }
        return ans + cnt;

    }
};
