class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0;
        if(n%2==1 && m%2==1){
            if(grid[n/2][m/2]==1){
                grid[n/2][m/2]=0;
                ans++;
            }
        }
        for(int i=0;i<(n/2);i++){
            for(int j=0;j<(m/2);j++){
                int k=0;
                k+=grid[i][j];
                k+=grid[n-1-i][j];
                k+=grid[i][m-1-j];
                k+=grid[n-1-i][m-1-j];
                if(k%2==0){
                    if(k%4!=0){
                        ans+=2;
                    }
                }
                else{
                    ans++;
                }
            }
        }
        bool ok=false;
        int c=0;
        if(n%2==1){
            int i=n/2;
            int l=0,r=(m-1);
            while(l<r){
                if(grid[i][l]!=grid[i][r]){
                    ok=true;
                    ans++;
                    c+=2;
                }
                else{
                    if(grid[i][l]==1){
                        c+=2;
                    }
                }
                l++;
                r--;
            }
        }
        if(m%2==1){
            int j=m/2;
            int u=0,d=(n-1);
            while(u<d){
                if(grid[u][j]!=grid[d][j]){
                    ok=true;
                    ans++;
                    c+=2;
                }
                else{
                    if(grid[u][j]==1){
                        c+=2;
                    }
                }
                u++;
                d--;
            }
        }
        if(c%4!=0){
            if(!ok){
                ans+=2;
            }
        }
        return ans;
    }
};
