class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n= grid.size(), m= grid[0].size();
        int ans = 0;
        for(int i=0; i<(n/2); i++)
        {
            for(int j=0; j<(m/2); j++)
            {
                int z=0, o=0;
                if(grid[i][j]==0)z++;
                else o++;
                if(grid[i][m-j-1]==0)z++;
                else o++;
                if(grid[n-i-1][j]==0)z++;
                else o++;
                if(grid[n-i-1][m-j-1]==0)z++;
                else o++;

                ans = ans + min(o,z);
            }
        }
        if(n%2 && m%2)
        {
            if(grid[n/2][m/2]==1)ans++;
            int zo=0, oo=0;
            for(int j=0; j<(m/2); j++)
            {
                int i = (n/2);
                if(grid[i][j]!=grid[i][m-j-1])
                {
                    zo++;
                }
                else if(grid[i][j]==1 &&grid[i][m-j-1]==1)
                {
                    oo++;
                }
            }
            for(int i=0; i<(n/2); i++)
            {
                int j = (m/2);
                if(grid[i][j]!=grid[n-i-1][j])
                {
                    zo++;
                }
                else if(grid[i][j]==1 &&grid[n-i-1][j]==1)
                {
                    oo++;
                }
            }
            if(oo && zo){
                ans=ans+zo;
            }
            else if(oo==0 && zo)
            {
                ans=ans+zo;
            }
            else if(oo && zo==0)
            {
                if(oo%2==0);
                else ans=ans+2;
            }
        }
        else if(n%2)
        {
            int zo=0, oo=0;
            for(int j=0; j<(m/2); j++)
            {
                int i = (n/2);
                if(grid[i][j]!=grid[i][m-j-1])
                {
                    zo++;
                }
                else if(grid[i][j]==1 &&grid[i][m-j-1]==1)
                {
                    oo++;
                }
            }
            if(oo && zo){
                ans=ans+zo;
            }
            else if(oo==0 && zo)
            {
                ans=ans+zo;
            }
            else if(oo && zo==0)
            {
                if(oo%2==0);
                else ans=ans+2;
            }
        }
        else if(m%2)
        {
            int zo=0, oo=0;
            for(int i=0; i<(n/2); i++)
            {
                int j = (m/2);
                if(grid[i][j]!=grid[n-i-1][j])
                {
                    zo++;
                }
                else if(grid[i][j]==1 &&grid[n-i-1][j]==1)
                {
                    oo++;
                }
            }
            if(oo && zo){
                ans=ans+zo;
            }
            else if(oo==0 && zo)
            {
                ans=ans+zo;
            }
            else if(oo && zo==0)
            {
                if(oo%2==0);
                else ans=ans+2;
            }
        }
        return ans;
    }
};
