class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int row=grid.size();
        int col=grid[0].size();
        int res=0;
        if(row%2==1 && col%2==1) {
            if(grid[row/2][col/2]==1)
                res += 1;
        }
        vector<int> sft(3,0);
        if(row%2==1){
            int n=0,m=col-1;
            while(n<m){
                int sm=grid[row/2][n]+grid[row/2][m];
                sft[sm]+=1;
                n+=1;
                m-=1;
            }
        }
        if(col%2==1) {
            int n=0,m=row-1;
            while(n<m) {
                int sm=grid[n][col/2]+grid[m][col/2];
                sft[sm]+=1;
                n+=1;
                m-=1;
            }
        }
        if(sft[2]%2==1){
            if(sft[1]>0)
                res+=sft[1];
            else
                res+=2;
        } 
        else
            res += sft[1];
        for(int i=0;i<row/2;++i) {
            for(int j=0;j<col/2;++j) {
                int sum=grid[i][j]+ grid[row-1-i][j]+ grid[i][col-1-j]+ grid[row-1-i][col-1-j];
                if (sum!=0&&sum!=4) {
                    res+=min(sum,4-sum);
                }
            }
        }
        return res;
    }
};
