class Solution {
public:
    int calFlipCol(int col, vector<vector<int>> &grid) {
        int m = grid.size();
        int n = grid[0].size();
        int l = 0, r = m - 1;
        int count = 0;
        while(l < r) {
            if(grid[l][col] != grid[r][col])
                count++;
            l++, r--;
        }
        return count;
    }
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int minFlips = 0;
        for(int j = 0; j <  n / 2; j++) {
            int l = 0, r = m - 1;
            while(l < r) {
                vector<int> cnt(2, 0);
                cnt[grid[l][j]]++, cnt[grid[r][j]]++;
                cnt[grid[l][n - j - 1]]++, cnt[grid[r][n - j - 1]]++;
                // cout << l << " " << r << " " << cnt[0] << " " << cnt[1] << endl;
                minFlips += 4 - max(cnt[0], cnt[1]);
                // cout << minFlips << endl;
                l++, r--;
            }
        }
        // cout << "1 : " << minFlips << endl;
        int cnt1 = 0;
        if(n % 2 == 1 && m % 2 == 1) {
            minFlips += grid[m/2][n/2];
        }
        // cout << "2 : " << minFlips << endl;
        int totalFlips = 0;
        if(n % 2 == 1) {
            int l = 0, r = m - 1;
            while(l < r) {
                if(grid[l][ n / 2] != grid[r][n / 2])
                    totalFlips++;
                else if(grid[l][n / 2] == grid[r][n / 2] && grid[r][n / 2] == 1)
                    cnt1 += 2;
                l++, r--;
            }
        }
        // cout << "1 : " << totalFlips << endl;
        if(m % 2 == 1) {
            int l = 0, r = n - 1;
            while(l < r) {
                if(grid[m / 2][l] != grid[m / 2][r])
                    totalFlips++;
                else if(grid[m / 2][l] == grid[m / 2][r] && grid[m / 2][r] == 1)
                    cnt1 += 2;
                l++, r--;
            }
        }
        // cout << "2 : " << totalFlips << endl;
        if(totalFlips == 0 && cnt1 % 4 != 0)
            totalFlips += 2;
        return minFlips + totalFlips;
    }
};
