class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n=len(grid)
        m=len(grid[0])
        res=0
        for i in range(n//2):
            for j in range(m//2):
                c=grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1]
                if c==2:
                    res+=2
                elif c==1 or c==3:
                    res+=1
        if n&1 and m&1:
            if grid[n//2][m//2]:
                res+=1
        c=0
        p=0
        if n&1:
            for i in range(m//2):
                c+=(grid[n//2][i]!=grid[n//2][m-i-1])
                if grid[n//2][i] and grid[n//2][m-i-1]:
                    p+=2
        if m&1:
            for i in range(n//2):
                c+=(grid[i][m//2]!=grid[n-i-1][m//2])
                if grid[i][m//2] and grid[n-i-1][m//2]:
                    p+=2
        if p%4==2 and c==0:
            res+=2
        res+=c
        return res
