class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        vector<vector<int>> visited(n,vector<int>(m,0));
        vector<vector<int>> arr;//{one,zero}

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(visited[i][j]) continue;

                int x1 = i, y1 = j;
                int x2 = i, y2 = m-j-1;
                int x3 = n-i-1, y3 = j;
                int x4 = n-i-1, y4 = m-j-1;

                set<pair<int,int>> st;
                st.insert({x1,y1});
                st.insert({x2,y2});
                st.insert({x3,y3});
                st.insert({x4,y4});

                int cntOne = 0, cntZero = 0;
                for(auto &z : st){
                    int x = z.first, y = z.second;
                    if(grid[x][y]==1) cntOne++;
                    else cntZero++;
                    visited[x][y] = 1;
                }
                vector<int> temp = {(int)st.size(),cntOne,cntZero};
                arr.push_back(temp);
            }
        }
        int minCnt = 0;

        int cnt1 = 0, cnt2 = 0, cnt3 = 0;

        for(auto &v : arr){
            int sz = v[0], one = v[1], zero = v[2];
            if(sz==4) minCnt += (sz - max(one,zero));
            else if(sz==2){
                if(one==2) cnt1++;
                else if(zero==2) cnt2++;
                else cnt3++;
            }
            else if(sz==1 && one==1){
                minCnt++;
            }
        }
        minCnt += cnt3;
        if(cnt1&1){
            if(cnt3==0) minCnt += 2;
        }
       // cout << cnt1 << " " << cnt2 << " " << cnt3 << endl;
        return minCnt;
    }
};
