class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
          int n = g.size(), m = g[0].size();
    int a = 0;

    if (n % 2 == 1 && m % 2 == 1) {
        if (g[n / 2][m / 2] == 1) {
            a += 1;
        }
    }

    vector<int> s(3, 0);
    if (n % 2 == 1) {
        int x = 0, y = m - 1;
        while (x < y) {
            int sm = g[n / 2][x] + g[n / 2][y];
            s[sm] += 1;
            x += 1;
            y -= 1;
        }
    }

    if (m % 2 == 1) {
        int x = 0, y = n - 1;
        while (x < y) {
            int sm = g[x][m / 2] + g[y][m / 2];
            s[sm] += 1;
            x += 1;
            y -= 1;
        }
    }

    if (s[2] % 2 == 1) {
        if (s[1] > 0) {
            a += s[1];
        } else {
            a += 2;
        }
    } else {
        a += s[1];
    }

    for (int i = 0; i < n / 2; ++i) {
        for (int j = 0; j < m / 2; ++j) {
            int x1 = g[i][j];
            int x2 = g[n - 1 - i][j];
            int x3 = g[i][m - 1 - j];
            int x4 = g[n - 1 - i][m - 1 - j];

            int sm = x1 + x2 + x3 + x4;

            if (sm != 0 && sm != 4) {
                a += min(sm, 4 - sm);
            }
        }
    }

    return a;
    }
};
