class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        int ans = 0;

        if (m % 2 == 1 && n % 2 == 1) {
            ans += centerFlip(grid, m, n);
        }

        int[] shift = new int[3];
        if (m % 2 == 1) {
            horMidCol(grid, m, n, shift);
        }

        if (n % 2 == 1) {
            midCol(grid, m, n, shift);
        }

        ans += shiftFlips(shift);

        ans += calcQuadFlip(grid, m, n);

        return ans;
    }

    private int centerFlip(int[][] grid, int m, int n) {
        return grid[m / 2][n / 2] == 1 ? 1 : 0;
    }

    private void horMidCol(int[][] grid, int m, int n, int[] shift) {
        int a = 0, b = n - 1;
        while (a < b) {
            int sm = grid[m / 2][a] + grid[m / 2][b];
            shift[sm] += 1;
            a += 1;
            b -= 1;
        }
    }

    private void midCol(int[][] grid, int m, int n, int[] shift) {
        int a = 0, b = m - 1;
        while (a < b) {
            int sm = grid[a][n / 2] + grid[b][n / 2];
            shift[sm] += 1;
            a += 1;
            b -= 1;
        }
    }

    private int shiftFlips(int[] shift) {
        if (shift[2] % 2 == 1) {
            return shift[1] > 0 ? shift[1] : 2;
        } else {
            return shift[1];
        }
    }

    private int calcQuadFlip(int[][] grid, int m, int n) {
        int flips = 0;
        for (int i = 0; i < m / 2; ++i) {
            for (int j = 0; j < n / 2; ++j) {
                int a = grid[i][j];
                int b = grid[m - 1 - i][j];
                int c = grid[i][n - 1 - j];
                int d = grid[m - 1 - i][n - 1 - j];

                int sm = a + b + c + d;

                if (sm != 0 && sm != 4) {
                    flips += Math.min(sm, 4 - sm);
                }
            }
        }
        return flips;
    }
}
