class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int rr=0,cc=0;
        int n=g.size(),m=g[0].size();
        int o=0,eq=0,ans=0,eq1=0,ct=0;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                if((n-i-1)==i)
                {
                    if(g[i][j]!=g[i][(m-j-1)]) 
                    {
                        eq++;
                    }
                    else if(j!=(m-j-1) && g[i][j]==1) ct++;
                    else if((j==(m-j-1)) && g[i][j]==1) eq1++;
                }
                else if((m-j-1)==j)
                {
                    if(g[i][j]!=g[(n-i-1)][j]) eq++;
                    else if(g[i][j]==1)
                    {
                        ct++;
                    }
                }
                else
                {
                    int ct=0;
                    if(g[(n-i-1)][j]!=g[i][j]) ct++;
                    if(g[i][(m-j-1)]!=g[i][j]) ct++;
                    if(g[i][j]!=g[(n-i-1)][(m-j-1)]) ct++;
                    if(ct==3) ct-=2;
                    ans+=ct;
                }
                //cout<<ans<<endl;
            }
        }
        ans/=4;
        eq=(eq)/2;
        if(ct%4!=0 && eq==0) ans+=2;
        return ans+eq+eq1;

    }
};
