class Solution {
public:
    int minFlips(vector<vector<int>>& g) 
    {
        int n=g.size();
        int m=g[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
            int c=0;    
            if(g[i][j]==1)
                c++;
            if(g[i][m-1-j]==1)
                c++;
            if(g[n-1-i][j]==1)
                c++;
            if(g[n-1-i][m-1-j]==1)
                c++;
            c=min(c,4-c);
            ans+=c;
            }
        }
        // cout<<ans<<endl;
        int ans2=0;
        int noone=0;
        if(n%2!=0)
        {
             for(int j=0;j<m/2;j++)
            {
            if(g[n/2][j]!=g[n/2][m-1-j])
                ans2++;
            else if(g[n/2][j]==1)
                noone+=2;

            }
        }
        if(m%2!=0)
        {
            for(int i=0;i<n/2;i++)
            {
            if(g[i][m/2]!=g[n-1-i][m/2])
                ans2++;
            else if(g[i][m/2]==1)
                noone+=2;
            }
        }
        if(ans2==0 && noone%4!=0)
            ans2+=2;

        if(n%2!=0 && m%2!=0)
        {
            if(g[n/2][m/2]==1)
                ans2++;
        }
        return ans+ans2;


    }
};
