class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = 0
        for i in range(m//2):
            for j in range(n//2):
                cnt = [0, 0]
                cnt[grid[i][j]] += 1
                cnt[grid[m-1-i][j]] += 1
                cnt[grid[i][n-1-j]] += 1
                cnt[grid[m-1-i][n-1-j]] += 1
                ans += min(cnt[0], cnt[1])

        ones = 0
        ans2 = 0
        if m % 2 == 1:
            for i in range(n//2):
                ans2 += grid[m//2][i] != grid[m//2][n-1-i]
                ones += 2 if grid[m//2][i] or grid[m//2][n-1-i] else 0
        if n % 2 == 1:
            for j in range(m//2):
                ans2 += grid[j][n//2] != grid[m-1-j][n//2]
                ones += 2 if grid[j][n//2] or grid[m-1-j][n//2] else 0
        # print(ones, ans2)
        if ones % 4 != 0 and ans2 == 0:
            ans2 = 2
        # print(ans, ans2)
        return ans + ans2 + (grid[m//2][n//2] if m % 2 and n % 2 else 0)
