class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(), ans = 0;
        map<pair<int, int>, int> mp;
        for (int i = 0; i < n; ++i)
        {
            for (int j = 0; j < m / 2; ++j)
            {
                if (grid[i][j] != grid[i][m - 1 - j])
                {
                    mp[{i, j}]++;
                    if (grid[i][m - 1 - j] == grid[n - 1 - i][m - 1 - j])
                        grid[i][j] = grid[i][m - 1 - j];
                    else
                        grid[i][m - 1 - j] = grid[i][j];
                    ans++;
                }
            }
        }

        for (int j = 0; j < m; ++j)
        {
            for (int i = 0; i < n / 2; ++i)
            {
                if (grid[i][j] != grid[n - 1 - i][j])
                {
                    mp[{i, j}]++;
                    grid[i][j] = grid[n - 1 - i][j];
                    ans++;
                }
            }
        }

        int ones = 0;
        for (auto &it : grid)
        {
            for (int val : it)
            {
                if (val == 1)
                    ones++;
            }
        }

        int rem = ones % 4;
        if (rem == 0) return ans;

        if (m % 2 == 0)
        {
            if (n % 2 == 1)
            {
                for (int j = 0; j <= m / 2; j++)
                {
                    if (mp.find({n / 2, j}) != mp.end())
                        return ans;
                }
                return ans + 2;
            }
        }
        else
        {
            if (n % 2 == 0)
            {
                for (int j = 0; j <= n / 2; j++)
                {
                    if (mp.find({j, m / 2}) != mp.end())
                        return ans;
                }
                return ans + 2;
            }
            else
            {
                if (grid[n / 2][m / 2])
                {
                    ans++;
                    rem--;
                    if (rem == 0)
                        return ans;
                }

                for (int j = 0; j <= n / 2; j++)
                {
                    if (mp.find({j, m / 2}) != mp.end())
                    {
                        return ans;
                    }
                }
                    for (int j = 0; j <= m / 2; j++)
                    {
                        if (mp.find({n / 2, j}) != mp.end())
                        {
                            return ans;
                        }
                    }
                return ans + 2;
            }
        }

        return ans;
    }
};
