func minFlips(grid [][]int) int {
    ans := 0
    m, n := len(grid), len(grid[0])
    x, y := m/2, n/2
    for i := 0; i < x; i++ {
        for j := 0; j < y; j++ {
            cnt := grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-1-j]
            ans += min(cnt, 4-cnt)
        }
    }
    curr := 0
    can := 0
    if m & 1 != 0 {
        for j := 0; j < y; j++ {
            if grid[x][j] == grid[x][n-1-j] {
                curr += grid[x][j]+grid[x][j]
            } else {
                ans++
                can += 2
            }
        }
    }
    if n & 1 != 0 {
        for i := 0; i < x; i++ {
            if grid[i][y] == grid[m-1-i][y] {
                curr += grid[i][y]+grid[i][y]
            } else {
                ans++
                can += 2
            }
        }
    }
    if m & 1 != 0 && n & 1 != 0 {
        if grid[x][y] == 1 {
            ans++
        }
    }
    if curr % 4 == 0 || can >= 2 {
        return ans
    }
    return ans+2
}
