class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])

        ans = 0
        for i in range(m//2):
            for j in range(n//2):
                ones = grid[i][j] + grid[~i][j] + grid[i][~j] + grid[~i][~j]
                ans += min(ones, 4 - ones)
                # print(f'{i=}, {j=} {ones=} {ans=}')



        chg = 0
        ones = 0
        if m&1:
            ones = sum(grid[m//2])
            if n&1:
                ones -= grid[m//2][n//2]
            chg += sum(grid[m//2][i] != grid[m//2][~i] for i in range(n//2))
        if n&1:
            ones += sum(grid[i][n//2] for i in range(m))
            chg += sum(grid[i][n//2] != grid[~i][n//2] for i in range(m//2))
            if m&1:
                ones -= grid[m//2][n//2]

        if m&1 and n&1:
            ans += grid[m//2][n//2]



        #print(ans, ones, chg)
        ans += chg
        if chg == 0 and ones%2 ==0 and ones%4 != 0:
            ans +=2



        return ans
