class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
    int n=grid.size(), m=grid[0].size(), ans=0;
    if(n%2 && m%2 && grid[n/2][m/2]==1) ans++;
    vector<int>vec(3,0);
    if(n%2) {
        int first=0, b=m-1;
        while(first<b) vec[grid[n/2][first++]+grid[n/2][b--]]++;
    }

    if(m%2) {
        int first=0, b=n-1;
        while(first<b)vec[grid[first++][m/2]+grid[b--][m/2]]++;
    }
    if(vec[2]%2)ans+=(vec[1]>0)?vec[1]:2;
    else ans+=vec[1];
    for(int i=0;i<n/2;i++)
        for(int j=0;j<m/2;j++) {
            int first=grid[i][j];
            int second=grid[n-1-i][j];
            int third=grid[i][m-1-j];
            int four=grid[n-1-i][m-1-j];
            int sol=first+second+third+four;
            if(sol&&sol!=4) ans=ans+min(sol,4-sol);
        }
    return ans;
    }
};
