class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        int fps = 0;
        if(m%2==1&&n%2==1&&grid[m/2][n/2]==1)fps++;
        vector<int> v(3, 0);
        if (m % 2 == 1) {
            int j = 0, k = n - 1;
            while (j < k) {
                int ind = grid[m / 2][j] + grid[m / 2][k];
                v[ind] += 1;
                j += 1;
                k -= 1;
            }
        }

        if (n % 2 == 1) {
            int j = 0, k = m - 1;
            while (j < k) {
                int ind = grid[j][n / 2] + grid[k][n / 2];
                v[ind] += 1;
                j += 1;
                k -= 1;
            }
        }

        if (v[2] % 2 == 1) {
            if (v[1] > 0) {
                fps += v[1];
            } else {
                fps += 2;
            }
        } else {
            fps += v[1];
        }

        for (int i = 0; i < m / 2; ++i) {
            for (int c = 0; c < n / 2; ++c) {
                int j = grid[i][c];
                int k = grid[m - 1 - i][c];
                int l = grid[i][n - 1 - c];
                int p = grid[m - 1 - i][n - 1 - c];

                int ind = j + k + l + p;

                if (ind != 0 && ind != 4) {
                    fps += min(ind, 4 - ind);
                }
            }
        }

        return fps;
    }
};
