class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n = v.size();
        int m = v[0].size();
        int ret = 0;

        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                vector<int> cnt(2);
                cnt[v[i][j]]++;
                cnt[v[n - 1 - i][j]]++;
                cnt[v[i][m - 1 - j]]++;
                cnt[v[n - 1 - i][m - 1 - j]]++;

                ret += min(cnt[0], cnt[1]);
            }
        }

        if ((n & 1) && (m & 1) && v[n / 2][m / 2] == 1) {
            v[n / 2][m / 2] = 0;
            ret++;
        }

        int one = 0;
        int diff1 = 0;
        int diff2 = 0;

        if (n & 1) {
            for (int j = 0; j < m / 2; j++) {
                diff1 += v[n / 2][j] != v[n / 2][m - 1 - j];
                one += v[n / 2][j] + v[n / 2][m - 1 - j];
            }
        }

        if (m & 1) {
            for (int i = 0; i < n / 2; i++) {
                diff2 += v[i][m / 2] != v[n - 1 - i][m / 2];
                one += v[i][m / 2] + v[n - 1 - i][m / 2];
            }
        }

        ret += diff1 + diff2;

        if (one % 4 && diff1 == 0 && diff2 == 0)
            ret += 2;

        return ret;
    }
};
