class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        rows, cols = len(grid), len(grid[0])
        result = 0
        if rows % 2 == 1 and cols % 2 == 1:
            if grid[rows // 2][cols // 2] == 1:
                result += 1

        adjustments = [0, 0, 0]
        if rows % 2 == 1:
            left, right = 0, cols - 1
            while left < right:
                total = grid[rows // 2][left] + grid[rows // 2][right]
                adjustments[total] += 1
                left += 1
                right -= 1
        if cols % 2 == 1:
            top, bottom = 0, rows - 1
            while top < bottom:
                total = grid[top][cols // 2] + grid[bottom][cols // 2]
                adjustments[total] += 1
                top += 1
                bottom -= 1
        if adjustments[2] % 2 == 1:
            if adjustments[1] > 0:
                result += adjustments[1]
            else:
                result += 2
        else:
            result += adjustments[1]
        for i in range(rows // 2):
            for j in range(cols // 2):
                val1 = grid[i][j]
                val2 = grid[rows - 1 - i][j]
                val3 = grid[i][cols - 1 - j]
                val4 = grid[rows - 1 - i][cols - 1 - j]

                total = val1 + val2 + val3 + val4

                if total != 0 and total != 4:
                    result += min(total, 4 - total)
        return result
