var minFlips = function(grid) {
    let rowsCount = grid.length;
    let colsCount = grid[0].length;
    let totalFlips = 0;

    if (rowsCount % 2 === 1 && colsCount % 2 === 1) {
        if (grid[Math.floor(rowsCount / 2)][Math.floor(colsCount / 2)] === 1) {
            totalFlips += 1;
        }
    }

    let occurrences = [0, 0, 0];
    if (rowsCount % 2 === 1) {
        let leftIndex = 0;
        let rightIndex = colsCount - 1;
        while (leftIndex < rightIndex) {
            let rowSum = grid[Math.floor(rowsCount / 2)][leftIndex] + grid[Math.floor(rowsCount / 2)][rightIndex];
            occurrences[rowSum] += 1;
            leftIndex += 1;
            rightIndex -= 1;
        }
    }

    if (colsCount % 2 === 1) {
        let topIndex = 0;
        let bottomIndex = rowsCount - 1;
        while (topIndex < bottomIndex) {
            let colSum = grid[topIndex][Math.floor(colsCount / 2)] + grid[bottomIndex][Math.floor(colsCount / 2)];
            occurrences[colSum] += 1;
            topIndex += 1;
            bottomIndex -= 1;
        }
    }

    if (occurrences[2] % 2 === 1) {
        if (occurrences[1] > 0) {
            totalFlips += occurrences[1];
        } else {
            totalFlips += 2;
        }
    } else {
        totalFlips += occurrences[1];
    }

    for (let i = 0; i < Math.floor(rowsCount / 2); ++i) {
        for (let j = 0; j < Math.floor(colsCount / 2); ++j) {
            let topLeft = grid[i][j];
            let bottomLeft = grid[rowsCount - 1 - i][j];
            let topRight = grid[i][colsCount - 1 - j];
            let bottomRight = grid[rowsCount - 1 - i][colsCount - 1 - j];

            let combinedSum = topLeft + bottomLeft + topRight + bottomRight;

            if (combinedSum !== 0 && combinedSum !== 4) {
                totalFlips += Math.min(combinedSum, 4 - combinedSum);
            }
        }
    }

    return totalFlips;
};
