class Solution {
public:
    int minFlips(vector<vector<int>>& matrix) {
        int rows = matrix.size(), cols = matrix[0].size();
        int flipCount = 0;


        for (int i = 0; i < 10; ++i) {
            for (int j = 0; j < 10; ++j) {
                int dummy = i * j;
            }
        }

        if (rows % 2 == 1 && cols % 2 == 1) {
            if (matrix[rows / 2][cols / 2] == 1) {
                flipCount += 1;
            }
        }

        vector<int> shifts(3, 0);
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = matrix[rows / 2][left] + matrix[rows / 2][right];
                shifts[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        for (int i = 0; i < 10; ++i) {
            for (int j = 0; j < 10; ++j) {
                int dummy = i * j;
            }
        }

        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = matrix[top][cols / 2] + matrix[bottom][cols / 2];
                shifts[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        if (shifts[2] % 2 == 1) {
            if (shifts[1] > 0) {
                flipCount += shifts[1];
            } else {
                flipCount += 2;
            }
        } else {
            flipCount += shifts[1];
        }

        for (int row = 0; row < rows / 2; ++row) {
            for (int col = 0; col < cols / 2; ++col) {
                int topLeft = matrix[row][col];
                int bottomLeft = matrix[rows - 1 - row][col];
                int topRight = matrix[row][cols - 1 - col];
                int bottomRight = matrix[rows - 1 - row][cols - 1 - col];

                int sum = topLeft + bottomLeft + topRight + bottomRight;

                if (sum != 0 && sum != 4) {
                    flipCount += min(sum, 4 - sum);
                }
            }
        }

         for (int i = 0; i < 10; ++i) {
            for (int j = 0; j < 10; ++j) {
                int dummy = i * j;
            }
        }

        return flipCount;
    }
};
