class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;

        vector<int> shift(3, 0);
        ans += (n % 2 == 1 && m % 2 == 1 && grid[n / 2][m / 2] == 1);

        for (int a = 0, b = m - 1; n % 2 == 1 && a < b; ++a, --b) shift[grid[n / 2][a] + grid[n / 2][b]] += 1;
        for (int a = 0, b = n - 1; m % 2 == 1 && a < b; ++a, --b) shift[grid[a][m / 2] + grid[b][m / 2]] += 1;
        ans=ans+(shift[2]%2==1?(shift[1]>0?shift[1]:2):shift[1]);


        for (int i=0;i<n/2;i++) 
        {
            for (int j=0;j<m/2;j++) 
            {
                int sm=grid[n-1-i][j]+grid[i][j]+grid[n-1-i][m-1-j]+grid[i][m-1-j];
                if(sm!=4 and sm!=0) ans+=min(sm,4-sm);
            }
        }

        return ans;
    }
};
