#define ll long long int

class Solution {
public:

    int minFlips(vector<vector<int>>& a) {
        ll n = a.size();
        ll m = a[0].size();

        ll ans = 0;

        for(ll i = 0; i<n/2; i++){
            for(ll j = 0; j<m/2; j++){
                vector<ll> v (2);

                v[a[i][j]]++;
                v[a[i][m - 1 - j]]++;
                v[a[n - 1 - i][j]]++;
                v[a[n - 1 - i][m - 1 - j]]++;

                ans += min(v[0], v[1]);
            }
        }

        ll c0 = 0;
        ll c1 = 0;

        bool hasChoice = false;

        if(n % 2){
            ll i = n/2;

            for(ll j = 0; j<m/2; j++){
                vector<ll> v (2);

                v[a[i][j]]++;
                v[a[i][m - 1 - j]]++;

                if(v[0] == v[1]){
                    ans++;
                    hasChoice = true;
                }
                else{
                    c0 += v[0];
                    c1 += v[1];
                }
            }
        }

        if(m % 2){
            ll j = m/2;

            for(ll i = 0; i<n/2; i++){
                vector<ll> v (2);

                v[a[i][j]]++;
                v[a[n - 1 - i][j]]++;

                if(v[0] == v[1]){
                    ans++;
                    hasChoice = true;
                }
                else{
                    c0 += v[0];
                    c1 += v[1];
                }
            }
        }

        if((n % 2) && (m % 2)){
            if(a[n/2][m/2]){
                ans++;
            }
        }

        if(!hasChoice){
            ans += c1 % 4;
        }

        return ans;
    }
};
