class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n=grid[0].size();
        int cnt = 0;
        int ones = 0;
        for(int i=0; i<m/2; i++)
        {
            for(int j=0; j<n/2; j++)
            {
                int temp = grid[i][j] + grid[i][n-j-1] + grid[m-i-1][j] + grid[m-i-1][n-j-1];
                if(temp > 2) ones += 4;
                if(temp == 2) cnt += 2;
                else cnt += (temp % 2);
            }
        }

        int prevCnt = 0;
        if(m % 2 == 1)
        {
            for(int j=0; j<n/2; j++)
            {
                int temp = grid[m/2][j] + grid[m/2][n-j-1];
                if(temp == 2) ones += 2;
                else
                {
                    cnt += (temp % 2);
                    if(temp != 0) prevCnt++;
                    if(temp != 0) ones += 2;
                }
            }
        }

        if(n % 2 == 1)
        {
            for(int i=0; i<m/2; i++)
            {
                int temp = grid[m-i-1][n/2] + grid[i][n/2];
                if(temp == 2) ones += 2;
                else{
                    cnt += (temp % 2);
                    if(temp != 0) prevCnt++;
                    if(temp != 0) ones += 2;
                }
            }
        }
        if((m%2 == 1) && (n%2 == 1)) ones += grid[m/2][n/2];
        if(ones % 2 == 1)
        {
            ones--;
            cnt++;
        }
        if(ones % 4 == 0) return cnt;
        if(prevCnt > 0) return cnt;
        return cnt + 2;
    }
};
