class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0,count=0,sum=-1;
        for(int i=0;i<(n+1)/2;i++){
            bool f1=0,f2=0,f3=0;
            for(int j=0;j<(m+1)/2;j++){
                int c0=0,c1=0;
                if(grid[i][j]) c0++; else c1++;
                if(i!=n-1-i && grid[n-i-1][j]) c0++; else if(i!=n-1-i) c1++; else f1=1;
                if(n-i-1!=i && m-j-1!=j && grid[n-i-1][m-j-1]) c0++; else if(n-i-1!=i && m-j-1!=j) c1++; else f2=1;
                if(j!=m-1-j && grid[i][m-1-j]) c0++; else if(j!=m-1-j) c1++; else f3=1;

                if(f1==0 && f2==0 && f3==0) ans+=min(c0,c1);
                else if(f1 && f2 && f3 && c0) ans++;
                else if(c1<=c0){
                    count++;
                    sum=max(sum,c1);
                    ans+=c1;
                }
                //cout<<f1<<f3<<i<<j<<" "<<count<<" "<<ans<<endl;
            }
        } 
        if(count%2==1) {if(sum==0) ans+=2;}                                                                                                                                                                                                                                                                                                               ;
        //cout<<count<<ans<<endl;
        return ans;
    }
};
