#include <bits/stdc++.h>
using namespace std;

typedef vector<int> vi;
typedef vector<vi> vvi;

#define ALL(v) v.begin(), v.end()
#define SIZE(v) (int)v.size()
#define MIN(a, b) a = min(a, b)
#define REP(i, n) for (int i = 0; i < (n); ++i)
#define FOR(a, b, c) for (int (a) = (b); (a) < (c); ++(a))

class Solution {
public:
    int minFlips(vvi &grid) {
        int n = SIZE(grid), m = SIZE(grid[0]);
        int flips = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                flips += 1;
            }
        }

        vi flipCount(3, 0);

        if (n % 2 == 1) {
            int left = 0, right = m - 1;
            while (left < right) {
                int sum = grid[n / 2][left] + grid[n / 2][right];
                flipCount[sum] += 1;
                left++;
                right--;
            }
        }

        if (m % 2 == 1) {
            int top = 0, bottom = n - 1;
            while (top < bottom) {
                int sum = grid[top][m / 2] + grid[bottom][m / 2];
                flipCount[sum] += 1;
                top++;
                bottom--;
            }
        }

        if (flipCount[2] % 2 == 1) {
            if (flipCount[1] > 0) {
                flips += flipCount[1];
            } else {
                flips += 2;
            }
        } else {
            flips += flipCount[1];
        }

        REP(i, n / 2) {
            REP(j, m / 2) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int sum = a + b + c + d;

                if (sum != 0 && sum != 4) {
                    flips += min(sum, 4 - sum);
                }
            }
        }

        return flips;
    }
};
