class Solution {
public:
    int minFlips(vector<vector<int>>& matrix) 
    {
       int rows = matrix.size();
        int cols = matrix[0].size();  
        int flipCount = 0;
        for (int i = 0; i < rows / 2; ++i) 
        {
            for (int j = 0; j < cols / 2; ++j) 
            {
                vector<int> elements = 
                {
                    matrix[i][j],
                    matrix[rows - i - 1][j],
                    matrix[i][cols - j - 1],
                    matrix[rows - i - 1][cols - j - 1]
                };
                int oneCount = count(elements.begin(), elements.end(), 1);
                flipCount += min(oneCount, 4 - oneCount);
            }
        }
        int symmetryPossible = 0;
        int oneMiddleCount = 0;
        if (rows % 2 != 0) 
        {
            for (int j = 0; j < cols / 2; ++j) 
            {
                if (matrix[rows / 2][j] != matrix[rows / 2][cols - j - 1]) 
                {
                    flipCount += 1;
                    symmetryPossible += 1;
                } 
                else 
                {
                    if (matrix[rows / 2][j] == 1) 
                    {
                        oneMiddleCount += 1;
                    }
                }
            }
        }

        if (cols % 2 != 0) {
            for (int i = 0; i < rows / 2; ++i) {
                if (matrix[i][cols / 2] != matrix[rows - i - 1][cols / 2]) {
                    flipCount += 1;
                    symmetryPossible += 1;
                } else {
                    if (matrix[i][cols / 2] == 1) {
                        oneMiddleCount += 1;
                    }
                }
            }
        }
        if (rows % 2 != 0 && cols % 2 != 0 && matrix[rows / 2][cols / 2] == 1) {
            flipCount += 1;
        }
        if (symmetryPossible == 0 && oneMiddleCount % 2 != 0) {
            flipCount += 2;
        }
        return flipCount;
    }
};
