class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        vector<int> cal(3, 0);
        int n = grid.size(), m = grid[0].size();
        int ans = 0;

        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int sm = grid[n / 2][a] + grid[n / 2][b];
                cal[sm] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int sm = grid[a][m / 2] + grid[b][m / 2];
                cal[sm] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (cal[2] % 2 == 1) {
            if (cal[1] > 0) {
                ans += cal[1];
            } else {
                ans += 2;
            }
        } else {
            ans += cal[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {

                int x1 = grid[i][m - 1 - j];
                int x2 = grid[n - 1 - i][m - 1 - j];
                int x3 = grid[i][j];
                int x4 = grid[n - 1 - i][j];

                int sm = x1+x2+x3+x4;

                if (sm != 0 && sm != 4) {
                    ans += min(sm, 4 - sm);
                }
            }
        }
        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }
        return ans;
    }
};
