class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int c=0;
        int row=grid.size();
        int col=grid[0].size();
        for(int i=0;i<row/2;i++){
            for(int j=0;j<col/2;j++){
                int one=0,zero=0;
                if(grid[i][j]==0){
                    zero++;
                }
                else{
                    one++;
                }
                if(grid[i][col-j-1]==0){
                    zero++;
                }
                else{
                    one++;
                }
                if(grid[row-i-1][j]==0){
                    zero++;
                }
                else{
                    one++;
                }
                if(grid[row-i-1][col-j-1]==0){
                    zero++;
                }
                else{
                    one++;
                }
                c+=min(zero,one);
            }
        }
        int sec=0,con=0;
        if(row%2!=0){
            int a=(row/2);
            int s=0,e=col-1;
            while(s<e){
                if(grid[a][s]!=grid[a][e]){
                    c++;
                    con++;
                }
                if(grid[a][s]==1 && grid[a][e]==1){
                    sec+=2;
                }
                s++;
                e--;
            }
        }
        if(col%2!=0){
            int a=(col/2);
            int s=0,e=row-1;
            while(s<e){
                if(grid[s][a]!=grid[e][a])
                {
                    c++;
                    con++;
                }
                if(grid[s][a]==1 && grid[e][a]==1){
                    sec+=2;
                }
                s++;
                e--;
            }
        }
        if(row%2!=0 && col%2!=0){
            if(grid[(row/2)][(col/2)]==1){
                c++;
            }
        }
        if(sec%4==0){
            return c;
        }
        else{
            if(con>0){
                return c;
            }
            else{
                return c+2;
            }
        }
    }
};
