class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        rows = len(grid)
        cols = len(grid[0])
        ret = 0
        top = rows // 2
        left = cols // 2
        for i in range(top):
            for j in range(left):
                count = [0, 0]
                count[ grid[i][j] ] += 1
                count[ grid[rows-1-i][j] ] += 1
                count[ grid[i][cols-1-j] ] += 1
                count[ grid[rows-1-i][cols-1-j] ] += 1
                print(count)
                ret += min(count)

        flag = False 

        mismatched = 0
        one_pairs = 0
        if rows % 2 == 1:
            l = 0 
            r = cols - 1
            while l < r:
                if grid[top][l] != grid[top][r]:
                    mismatched += 1
                else:
                    one_pairs += grid[top][l]
                l += 1
                r -= 1      
            if l == r:
                flag = flag or grid[top][l]             
        if cols % 2 == 1:
            l = 0 
            r = rows - 1
            while l < r:
                if grid[l][left] != grid[r][left]:
                    mismatched += 1
                else:
                    one_pairs += grid[l][left]
                l += 1
                r -= 1
            if l == r:
                flag = flag or grid[l][left]   

        if one_pairs % 2 != 0:
            if mismatched < 1:
                ret += 2
        ret += mismatched


        if flag:
            ret += 1


        return ret
