class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n  = v.size();
        int m = v[0].size();
        int cnt = 0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int z =v[i][j]+v[n-1-i][j]+v[i][m-1-j]+v[n-1-i][m-1-j];
                cnt+= min(z,4-z);
            }
        }
        int zz = 0 , oo = 0 , oz = 0;
        if(m&1)
            for(int i=0;i<n/2;i++)
            {
                if(v[i][m/2] != v[n-1-i][m/2]) oz++;
                else 
                {
                    if(v[i][m/2]==0)zz++;
                    else oo++;
                }
            }
        if(n&1)
        {
             for(int i=0;i<m/2;i++)
            {
                if(v[n/2][i] != v[n/2][m-1-i]) oz++;
                else 
                {
                    if(v[n/2][i]==0)zz++;
                    else oo++;
                }
            }
        }
        if((n&1) && (m&1))
        {
            cnt+=v[n/2][m/2];
        }
        cnt+=oz;
        if((oo&1) && oz==0)cnt+=2;
        return cnt;
    }
};
