class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int res = 0;
        int seen_2 = 0;
        for (int i = 0; i < m/2; i++) {
            for (int j = 0; j < n/2; j++) {
                int ref_row = m-i-1;
                int ref_col = n-j-1;
                int sum = grid[i][j] + grid[i][ref_col] + grid[ref_row][j] + grid[ref_row][ref_col];
                if (sum == 3 || sum == 1) {
                    res++;
                }
                else if (sum == 2) {
                    res += 2;
                }
            }
        }
        int ones = 0;
        if (m % 2) {
            for (int j = 0; j < n/2; j++) {
                int ref_col = n-j-1;
                ones += grid[m/2][j] + grid[m/2][ref_col];
                if (grid[m/2][j] != grid[m/2][ref_col]) {
                    res++;
                    seen_2++;
                }
            }
        }
        if (n % 2) {
            for (int i = 0; i < m/2; i++) {
                int ref_row = m-i-1;
                ones += grid[i][n/2] + grid[ref_row][n/2];
                if (grid[i][n/2] != grid[ref_row][n/2]) {
                    res++;
                    seen_2++;
                }
            }
        }
        if ((n % 2) && (m % 2)) {
            ones += grid[m/2][n/2];
        }
        if (seen_2 % 2 == 0 && seen_2 != 0) {
            return res + (ones % 2);
        }
        if (seen_2 == 1) {
            if (ones % 4 == 2 || ones % 4 == 0) {
                return res+1;
            }
            return res;
        }
        if (seen_2 % 2 == 1) {
            return res + (ones % 2 == 0);
        }
        return res + (ones % 4);
    }
};
