class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int r=g.size(),c=g[0].size(),f=0,s=0,e=0;
        f+= calcQ(g,r,c,s,e);
        if(r%2!=0) f+=calcMR(g,r,c,s,e);
        if(c%2!=0) f+=calcMC(g,r,c,s,e);
        if(r%2!=0&&c%2!=0) f+=g[r/2][c/2];
        if(s>0||e%4==0) return f;
        return f+2;
    }

private:
    int calcQ(vector<vector<int>>& g,int r,int c,int& s,int& e) {
        int q=0;
        for(int i=0;i<r/2;++i)
            for(int j=0;j<c/2;++j) {
                int sum=g[i][j] + g[i][c-1-j] + g[r-1-i][j] + g[r-1-i][c-1-j];
                q+= min(sum,4-sum);
            }
        return q;
    }

    int calcMR(vector<vector<int>>& g,int r,int c,int& s,int& e) {
        int mR=0,mid=r/2;
        for(int j=0;j<c/2;++j) {
            int sum= g[mid][j] + g[mid][c-1-j];
            mR+= min(sum,2-sum);
            if(sum==1) s++;
            if(sum>1) e+=2;
        }
        return mR;
    }

    int calcMC(vector<vector<int>>& g,int r,int c,int& s,int& e) {
        int mC=0,mid=c/2;
        for(int i=0;i<r/2;++i) {
            int sum=g[i][mid] + g[r-1-i][mid];
            mC+=min(sum,2-sum);
            if(sum==1)s++;
            if(sum>1)e+=2;
        }
        return mC;
    }
};
