class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int il = 0, ir = n-1;
        int ans = 0;
        int extra = 0;
        int two = 0;
        while(il <= ir){
            int jl = 0, jr = m-1;
            while(jl <= jr){
                if(il == ir && jl == jr && grid[il][jl])ans++;

                else if(il == ir && jl!= jr){
                    int temp = grid[il][jl] + grid[il][jr];
                    if(temp == 2){
                        two++;
                    }
                    else if(temp == 1){
                        extra++; ans++;
                    }
                }
                else if(jl == jr && il != ir){
                    int temp = grid[il][jl] + grid[ir][jl];
                    if(temp == 2){
                        two++;
                    }
                    else if(temp == 1){
                        extra++; ans++;
                    }
                }
                else {
                    int temp = grid[il][jr] + grid[il][jl] + grid[ir][jl] + grid[ir][jr];
                    if(temp%2 != 0)ans++;
                    else if(temp == 2)ans+=2;
                }
                jl++; jr--;
            }
            il++; ir--;
        }
        if(two % 2 != 0){
            if(extra == 0)ans+= 2;
        }
        return ans;


    }
};
