import java.util.*;

class Solution {
    public int minFlips(int[][] matrix) {
        int rows = matrix.length;
        int cols = matrix[0].length;
        int flips = 0;

        // Handle the center cell if both dimensions are odd
        if (rows % 2 == 1 && cols % 2 == 1) {
            if (matrix[rows / 2][cols / 2] == 1) {
                flips += 1;
            }
        }

        // Array to count the number of mismatches
        int[] counts = new int[3];

        // Count flips required for rows if number of rows is odd
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = matrix[rows / 2][left] + matrix[rows / 2][right];
                counts[sum] += 1;
                left++;
                right--;
            }
        }

        // Count flips required for columns if number of columns is odd
        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = matrix[top][cols / 2] + matrix[bottom][cols / 2];
                counts[sum] += 1;
                top++;
                bottom--;
            }
        }

        // Adjust flips based on counts
        if (counts[2] % 2 == 1) {
            if (counts[1] > 0) {
                flips += counts[1];
            } else {
                flips += 2;
            }
        } else {
            flips += counts[1];
        }

        // Calculate flips required for 2x2 blocks
        for (int i = 0; i < rows / 2; i++) {
            for (int j = 0; j < cols / 2; j++) {
                int topLeft = matrix[i][j];
                int bottomLeft = matrix[rows - 1 - i][j];
                int topRight = matrix[i][cols - 1 - j];
                int bottomRight = matrix[rows - 1 - i][cols - 1 - j];

                int total = topLeft + bottomLeft + topRight + bottomRight;

                if (total != 0 && total != 4) {
                    flips += Math.min(total, 4 - total);
                }
            }
        }

        return flips;
    }
}
