class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int onecnt = 0, surplus = 0;
        if (grid.size() & 1) {
          int n = grid.size()/2;
            for(int i = 0; i < grid[n].size() / 2; ++i) { 
              onecnt += (grid[n][i] & grid[n][grid[n].size() - 1- i])*2;
              surplus += grid[n][i] ^ grid[n][grid[n].size() - 1- i];
            }
        }

        if (grid[0].size() & 1) {
          int n = grid[0].size()/2;
            for(int i = 0; i < grid.size() / 2; ++i) { 
              onecnt += (grid[i][n] & grid[grid.size() - 1- i][n])*2;
              surplus += grid[i][n] ^ grid[grid.size() - 1- i][n];
            }
        }

        if ((grid.size() & 1) && (grid[0].size() & 1)) {
          ans += grid[grid.size() /2][grid[0].size()/2];
        }

        if (onecnt % 4 != 0 && !surplus) {
          ans += 2;
        }

        for (int i = 0; i < grid.size() / 2; ++i) {
            for (int j = 0; j < grid[0].size() / 2; ++j) {
                int cnt = grid[i][j] + grid[i][grid[i].size() - 1 - j] +
                          grid[grid.size() - 1 - i][j] +
                          grid[grid.size() - 1 - i][grid[i].size() - 1 - j];

                if (cnt == 0 || cnt == 4) {
                    continue;
                } else if (cnt == 1 || cnt == 2) {
                    ans += cnt;
                } else if (cnt == 3) {
                    ++ans;
                }
            }
        }

        return ans + surplus;
    }
};
