class Solution {
public:
    int n, m;
    int minFlips(vector<vector<int>>& grid) {
        n = grid.size(), m = grid[0].size();
        int ans = 0;
        for(int i = 1; i <= n / 2; ++i){
            for(int j = 1; j <= m / 2; ++j){
                int buc[2] = {0, 0};
                ++buc[grid[i - 1][j - 1]], 
                ++buc[grid[n - i][m - j]],
                ++buc[grid[i - 1][m - j]],
                ++buc[grid[n - i][j - 1]];
                ans += std::min(buc[0], buc[1]);
            }
        }
        if(n % 2 == 0 && m % 2 == 0){
            return ans;
        }
        if(n % 2 == 0 && m % 2 == 1){
            int y = m / 2, flag = 0, c1 = 0;
            for(int i = 1; i <= n / 2; ++i){
                int t = 0;
                if(grid[i - 1][y] == 1) ++t;
                if(grid[n - i][y] == 1) ++t;
                if(t == 1) ans += 1, flag = 1;
                if(t == 2) c1 += 2;
            }
            if(!flag && c1 % 4 != 0) return ans + 2;
            else return ans;
        }
        if(n % 2 == 1 && m % 2 == 0){
            int x = n / 2, flag = 0, c1 = 0;
            for(int j = 1; j <= m / 2; ++j){
                int t = 0;
                if(grid[x][j - 1] == 1) ++t;
                if(grid[x][m - j] == 1) ++t;
                if(t == 1) ans += 1, flag = 1;
                if(t == 2) c1 += 2;
            }
            if(!flag && c1 % 4 != 0) return ans + 2;
            else return ans;
        }
        if(grid[n / 2][m / 2] == 1) ans += 1;
        int x = n / 2, y = m / 2, c1 = 0, flag = 0;
        for(int i = 1; i <= n / 2; ++i){
            int t = 0;
            if(grid[i - 1][y] == 1) ++t;
            if(grid[n - i][y] == 1) ++t;
            if(t == 1) ans += 1, flag = 1;
            if(t == 2) c1 += 2;
        }
        for(int j = 1; j <= m / 2; ++j){
            int t = 0;
            if(grid[x][j - 1] == 1) ++t;
            if(grid[x][m - j] == 1) ++t;
            if(t == 1) ans += 1, flag = 1;
            if(t == 2) c1 += 2;
        }
        if(!flag && c1 % 4 != 0) return ans + 2;
        return ans;
    }
};
